import React, { useState, useRef, useEffect } from "react";
import Recorder from "recorder-js";
import "./Chatbot.css";

const backendUrl = "http://localhost:5000";

const MedicalChatbot = () => {
  const [conversation, setConversation] = useState([]);
  const [diagnosis, setDiagnosis] = useState("");
  const [keySymptoms, setKeySymptoms] = useState("");
  const [followUps, setFollowUps] = useState("");
  const [recommendations, setRecommendations] = useState("");
  const [displayFollowUps, setDisplayFollowUps] = useState("");
  const [displayRecommendations, setDisplayRecommendations] = useState("");
  const [error, setError] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [displayedDiagnosis, setDisplayedDiagnosis] = useState("");
  const [isDiagnosisFinal, setIsDiagnosisFinal] = useState(false);
  const [spokenDiagnosis, setSpokenDiagnosis] = useState(false);
  const [spokenKeySymptoms, setSpokenKeySymptoms] = useState(false);

  const audioContextRef = useRef(null);
  const recorderRef = useRef(null);
  const silenceTimer = useRef(null);
  const speechRef = useRef(null);
  const fullDiagnosisRef = useRef("");
  const followUpsRef = useRef("");
  const recommendationsRef = useRef("");
  const keySymptomsRef = useRef("");

  useEffect(() => {
    if (fullDiagnosisRef.current && !isDiagnosisFinal) {
      revealAndSpeak(fullDiagnosisRef.current, setDisplayedDiagnosis);
      setIsDiagnosisFinal(true);
    }
  }, [diagnosis]);

  useEffect(() => {
    if (keySymptomsRef.current && !spokenKeySymptoms) {
      speakText(`Key symptoms identified: ${keySymptomsRef.current}`);
      setSpokenKeySymptoms(true);
    }
  }, [keySymptoms]);

  const revealAndSpeak = (text, setter) => {
    let index = 0;
    setter("");
    if (speechRef.current) window.speechSynthesis.cancel();
    const speech = new SpeechSynthesisUtterance(text);
    speech.lang = "en-US";
    speech.rate = 1;
    speech.pitch = 1;
    speechRef.current = speech;
    window.speechSynthesis.speak(speech);

    const interval = setInterval(() => {
      setter((prev) => prev + text[index]);
      index++;
      if (index >= text.length) clearInterval(interval);
    }, 30);
  };

  const handleStartRecording = async () => {
    setError(null);
    setIsRecording(true);
    setIsDiagnosisFinal(false);
    setSpokenDiagnosis(false);
    setSpokenKeySymptoms(false);

    try {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      recorderRef.current = new Recorder(audioContextRef.current);
      recorderRef.current.init(stream);
      recorderRef.current.start();

      const audioInput = audioContextRef.current.createMediaStreamSource(stream);
      const analyser = audioContextRef.current.createAnalyser();
      audioInput.connect(analyser);

      const checkSilence = () => {
        const buffer = new Float32Array(analyser.fftSize);
        analyser.getFloatTimeDomainData(buffer);
        const maxAmplitude = Math.max(...buffer.map(Math.abs));

        if (maxAmplitude < 0.01) {
          if (!silenceTimer.current) {
            silenceTimer.current = setTimeout(() => handleStopRecording(), 1500);
          }
        } else {
          clearTimeout(silenceTimer.current);
          silenceTimer.current = null;
        }
        requestAnimationFrame(checkSilence);
      };

      checkSilence();
    } catch (error) {
      setError("Microphone access denied or not available.");
      setIsRecording(false);
    }
  };

  const handleStopRecording = async () => {
    setIsRecording(false);
    setIsProcessing(true);
    clearTimeout(silenceTimer.current);

    try {
      const { blob } = await recorderRef.current.stop();
      const audioFile = new File([blob], "audio.wav", { type: "audio/wav" });
      const formData = new FormData();
      formData.append("file", audioFile);

      const transcriptionResponse = await fetch(`${backendUrl}/transcribe`, {
        method: "POST",
        body: formData,
      });

      const transcriptionData = await transcriptionResponse.json();
      if (transcriptionData.error) {
        setError(transcriptionData.error);
        setIsProcessing(false);
        return;
      }

      setConversation([
        { role: "AI", text: `Transcribed Text: ${transcriptionData.transcription}` },
      ]);

      const analysisResponse = await fetch(`${backendUrl}/analyze`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text: transcriptionData.transcription }),
      });

      const analysisData = await analysisResponse.json();
      const result = analysisData.analysis;

      const key = extractSection(result, "Key Symptoms Identified") || "No key symptoms mentioned.";
      const diag = extractSection(result, "Possible Medical Diagnosis") || "Diagnosis pending further details.";
      const follow = extractSection(result, "Follow-up Questions for Further Diagnosis") || "No follow-up questions provided.";
      const reco = extractSection(result, "Recommended Next Steps") || "No recommendations provided.";

      fullDiagnosisRef.current = diag;
      followUpsRef.current = follow;
      recommendationsRef.current = reco;
      keySymptomsRef.current = key;

      setKeySymptoms(key);
      setDiagnosis(diag);
    } catch (error) {
      setError("Error processing the audio.");
    }

    setIsProcessing(false);
  };

  const extractSection = (text, label) => {
    const safeLabel = label.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
    const regex = new RegExp(`\\*\\*${safeLabel}:\\*\\*\\s*([\\s\\S]*?)(?=\\n\\*\\*|$)`, "m");
    const match = text.match(regex);
    return match && match[1] ? match[1].replace(/undefined/g, "").trim() : null;
  };

  const speakText = (text) => {
    if (speechRef.current) window.speechSynthesis.cancel();
    const speech = new SpeechSynthesisUtterance(text);
    speech.lang = "en-US";
    speech.rate = 1;
    speech.pitch = 1;
    speechRef.current = speech;
    window.speechSynthesis.speak(speech);
  };

  const handleFollowUpsClick = () => {
    revealAndSpeak(followUpsRef.current, setDisplayFollowUps);
  };

  const handleRecommendationsClick = () => {
    revealAndSpeak(recommendationsRef.current, setDisplayRecommendations);
  };

  return (
    <div className="chatbot-container">
      <h1>Medical AI Chatbot</h1>
      {error && <p className="error-message">Error: {error}</p>}

      <div className="chatbox">
        {conversation.map((msg, index) => (
          <div key={index} className="message">
            <strong>{msg.role}: </strong> {msg.text}
          </div>
        ))}
      </div>

      {(keySymptoms || diagnosis) && (
        <div className="diagnosis-box">
          <h2>Diagnosis</h2>
          <p><strong>Key Symptoms Identified:</strong> {keySymptoms}</p>
          <p><strong>Possible Medical Diagnosis:</strong></p>
          <p>{displayedDiagnosis}</p>
          <br />
          <button onClick={handleFollowUpsClick}>Show Follow-up Questions</button>
          <button onClick={handleRecommendationsClick}>Show Recommendations</button>
          {displayFollowUps && (
            <div>
              <h4>Follow-up Questions:</h4>
              <p>{displayFollowUps}</p>
            </div>
          )}
          {displayRecommendations && (
            <div>
              <h4>Recommendations:</h4>
              <p>{displayRecommendations}</p>
            </div>
          )}
        </div>
      )}

      {!isRecording && !isProcessing && (
        <button onClick={handleStartRecording}>Start Recording</button>
      )}

      {isRecording && <p>üéôÔ∏è Listening... Speak now.</p>}
    </div>
  );
};

export default MedicalChatbot;
