import React, { useState, useRef, useEffect } from "react";
import Recorder from "recorder-js";  // Make sure this library is compatible
import "./Chatbot.css";
import nspell from "nspell";
import dictionary from "dictionary-en";

// Function to normalize repeating characters (fix typos)
const normalizeRepeats = (word) => word.replace(/(\w)\1{2,}/g, "$1$1");

export const fixTypos = async (text) => {
  return new Promise((resolve, reject) => {
    dictionary((err, dict) => {
      if (err) return reject(err);
      const spell = nspell(dict);

      const cleaned = text
        .split(" ")
        .map((word) => {
          const normalized = normalizeRepeats(word);
          if (!spell.correct(normalized)) {
            const suggestions = spell.suggest(normalized);
            return suggestions.length > 0 ? suggestions[0] : normalized;
          }
          return normalized;
        })
        .join(" ");
      resolve(cleaned);
    });
  });
};

const backendUrl = "http://localhost:5000";

const MedicalChatbot = () => {
  const [conversation, setConversation] = useState([]);
  const [diagnosis, setDiagnosis] = useState("");
  const [keySymptoms, setKeySymptoms] = useState("");
  const [followUps, setFollowUps] = useState("");
  const [recommendations, setRecommendations] = useState("");
  const [error, setError] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);

  const audioContextRef = useRef(null);
  const recorderRef = useRef(null);
  const silenceTimer = useRef(null);

  useEffect(() => {
    if (keySymptoms && !diagnosis) {
      setDiagnosis("Waiting for diagnosis...");
    }
  }, [keySymptoms]);

  const revealTextAndSpeak = (text, setter) => {
    if (!text) return;
    let index = 0;
    setter("");
    let fullText = "";

    const interval = setInterval(() => {
      const nextChar = text[index];
      fullText += nextChar;
      setter((prev) => prev + nextChar);
      index++;
      if (index >= text.length) {
        clearInterval(interval);
        const speech = new SpeechSynthesisUtterance(fullText);
        speech.lang = "en-US";
        speech.rate = 1;
        speech.pitch = 1;
        window.speechSynthesis.speak(speech);
      }
    }, 60);
  };

  const handleStartRecording = async () => {
    setError(null);
    setIsRecording(true);
    try {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      recorderRef.current = new Recorder(audioContextRef.current);
      await recorderRef.current.init(stream);
      recorderRef.current.start();

      const audioInput = audioContextRef.current.createMediaStreamSource(stream);

      const checkSilence = () => {
        if (!audioInput) return;
        requestAnimationFrame(checkSilence);
      };

      checkSilence();
    } catch (error) {
      setError("Microphone access denied or not available.");
      setIsRecording(false);
    }
  };

  const handleStopRecording = async () => {
    setIsRecording(false);
    setIsProcessing(true);
    clearTimeout(silenceTimer.current);

    try {
      const { blob } = await recorderRef.current.stop();
      const audioFile = new File([blob], "latest_audio.wav", { type: "audio/wav" });
      const formData = new FormData();
      formData.append("file", audioFile);

      const transcriptionResponse = await fetch(`${backendUrl}/transcribe`, {
        method: "POST",
        body: formData,
      });

      const transcriptionData = await transcriptionResponse.json();
      if (transcriptionData.error) {
        setError(transcriptionData.error);
        setIsProcessing(false);
        return;
      }

      setConversation([{ role: "AI", text: `Transcribed Text: ${transcriptionData.transcription}` }]);

      const analysisResponse = await fetch(`${backendUrl}/analyze`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text: transcriptionData.transcription }),
      });

      const analysisData = await analysisResponse.json();
      const result = analysisData.analysis;

      setKeySymptoms(result.keySymptoms || "No key symptoms.");
      setDiagnosis(result.diagnosis || "Diagnosis pending.");
    } catch (error) {
      setError("Server error: " + error.message);
    }

    setIsProcessing(false);
  };

  return (
    <div className="chatbot-container">
      <div className="chatbox">
        <div className="conversation">
          {conversation.map((message, index) => (
            <div key={index} className={`message ${message.role}`}>
              {message.text}
            </div>
          ))}
        </div>

        <div className="actions">
          <button onClick={handleStartRecording} disabled={isRecording || isProcessing}>
            {isRecording ? "Recording..." : "Start Recording"}
          </button>
          {isProcessing && <p>Processing...</p>}
          {error && <p className="error">{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default MedicalChatbot;
